import React, {Component} from 'react';
import '../styles/PersonalInfo.css';

class PersonalInfo extends Component {

  constructor(props) {
    super(props);

    this.changePhoto = this.changePhoto.bind(this);
  }

  changePhoto(e) {
    const fakeInput = document.createElement('input');
    fakeInput.type = 'file';
    fakeInput.click();

    fakeInput.addEventListener('change', () => {
      console.log(fakeInput.files[0]);
      console.log(new FileReader());
    });
  }

  render() {
    return(
      <section className='personal-info'>
        <NameAndPhoto clickedOnPhoto={this.changePhoto}/>
      </section>
    );
  }
}


class NameAndPhoto extends Component {

  render() {
    return(
      <div className='name-and-photo'>
        <div className='photo-box'>
          <img src={require('../images/profile.jpg')} alt='profile' onClick={this.props.clickedOnPhoto}/>
        </div>
        <div className='name-box'>
          <div className='first-name'>
            <input type='text' placeholder='First Name'/>
          </div>
          <div className='last-name'>
            <input type='text' placeholder='Last Name'/>
          </div>
        </div>
      </div>
    );
  }
}

export default PersonalInfo;
